query.sfwSql:
/* BABY TICKET SHOULD NOT COUNT ANYWHERE --open -> reassign -> clos */ --Assisted will include those parent contact tickets whose current status is closed and which are not reassigned to anybody at any point in time SELECT CT.CONTACT_TICKET_ID, 'Assisted' AS TICKET_TYPE,CT.CREATED_BY,CT.CREATED_DATE AS DATE_DATA_CREATED FROM SGT_CONTACT_TICKET CT INNER JOIN SGS_USER US ON CT.CREATED_BY=US.USER_ID WHERE (CT.CREATED_BY=CT.MODIFIED_BY) AND (((US.USER_SERIAL_ID=@USERID)AND (@USERID !=0)) OR (@USERID = 0)) AND (CT.CREATED_DATE BETWEEN @STARTDATE AND @ENDDATE) AND CT.STATUS_VALUE='CLOS' AND CT.ORIGINAL_CONTACT_TICKET_ID IS NULL AND NOT EXISTS (SELECT CONTACT_TICKET_HISTORY_ID FROM SGT_CONTACT_TICKET_HISTORY CTH WHERE CTH.CONTACT_TICKET_ID = CT.CONTACT_TICKET_ID AND CTH.STATUS_VALUE = 'RASS')
UNION ALL
--Transferred will include those parent contact tickets which are reassigned to anybody irrespective of current ticket status SELECT CT.CONTACT_TICKET_ID, 'Transferred' AS TICKET_TYPE,CT.CREATED_BY, CT.CREATED_DATE AS DATE_DATA_CREATED FROM SGT_CONTACT_TICKET CT INNER JOIN SGS_USER US ON CT.CREATED_BY=US.USER_ID WHERE ((US.USER_SERIAL_ID=@USERID) OR (@USERID = 0)) AND (CT.CREATED_DATE BETWEEN @STARTDATE AND @ENDDATE) AND CT.ORIGINAL_CONTACT_TICKET_ID IS NULL AND EXISTS (SELECT CONTACT_TICKET_HISTORY_ID FROM SGT_CONTACT_TICKET_HISTORY CTH WHERE CTH.CONTACT_TICKET_ID = CT.CONTACT_TICKET_ID AND CTH.STATUS_VALUE = 'RASS')

SELECT CON.CONTACT_TICKET_ID , CON.CREATED_DATE , CON.STATUS_VALUE, HIS.NOTES FROM SGT_CONTACT_TICKET CON WITH(NOLOCK) JOIN SGT_CONTACT_TICKET_HISTORY HIS WITH(NOLOCK) ON CON.CONTACT_TICKET_ID = HIS.CONTACT_TICKET_ID WHERE CON.PERSON_ID = @PERSON_ID AND HIS.CONTACT_TICKET_HISTORY_ID = (
SELECT TOP 1 HIST.CONTACT_TICKET_HISTORY_ID FROM SGT_CONTACT_TICKET_HISTORY HIST WITH(NOLOCK) WHERE HIST.CONTACT_TICKET_ID = CON.CONTACT_TICKET_ID
) AND (CON.STATUS_VALUE <> 'CLOS' OR (CON.STATUS_VALUE = 'CLOS' AND DATEDIFF(DAY,CON.MODIFIED_DATE,GETDATE()) <= 90 )) AND CON.IS_TICKET_CREATED_FROM_PORTAL_FLAG = 'Y' AND CON.CONTACT_TYPE_VALUE = 'PERS' ORDER BY CONTACT_TICKET_ID DESC
