query.sfwSql:
DECLARE @PERSON_ACCOUNT_ID INT;
SET @PERSON_ACCOUNT_ID = ( SELECT TOP 1 BPA.PERSON_ACCOUNT_ID FROM SGT_BENEFIT_APPLICATION BA JOIN SGT_BENEFIT_APPLICATION_PERSON_ACCOUNT BPA ON BPA.BENEFIT_APPLICATION_ID = BA.BENEFIT_APPLICATION_ID WHERE BA.BENEFIT_APPLICATION_ID = @BENEFIT_APPLICATION_ID AND BPA.IS_APPLICATION_PERSON_ACCOUNT_FLAG = 'Y' )
IF(@TRANSFER_FLAG = '') SET @TRANSFER_FLAG = NULL;
UPDATE SGT_PERSON_ACCOUNT_RETIREMENT_CONTRIBUTION SET TRANSFER_FLAG_VALUE = NULL, MODIFIED_BY = @USER_ID ,MODIFIED_DATE = GETDATE() ,UPDATE_SEQ = UPDATE_SEQ + 1 WHERE PERSON_ACCOUNT_ID = @PERSON_ACCOUNT_ID AND TRANSFER_FLAG_VALUE = ISNULL(@TRANSFER_FLAG,TRANSFER_FLAG_VALUE) AND ISNULL(TRANSFER_FLAG_VALUE,'') <> 'T'
